package com.javacode_11thMarh_2023;

public class SmallestBetween4DecimalNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double d1=1.1,d2=1.3,d3=1.4,d4=2.0;
		
		if(d1<d2 && d2<d3 && d3<d4) {
			System.out.println("Smallest number is "+d1);
		}else {
			System.out.println("Smallest number is not "+d1);
		}

	}

}
